{/* Implementing a stack using a singly linked list - witth the help of https://reactgo.com/datastructure-stacks-queues-javascript/*/}
{/* Declaring a class Node with two properties data and next */}
class Node{
  constructor(data) {
    this.data = data;
    this.next = null;
  }
}
/// Push, and declaring first, last and size properties of the stack 
/// Create method which accepts data as argument, declare a new node with that data, 
/// If a stack is empty set first and last node to newly created node, 
/// If there is data in stack then set new node.next to first property 
/// Update the first property with new node, then increment stack size by one 
class Stack{
  constructor(){
    this.first = null;
    this.last = null
    this.size = 0
  }

  push(data){

    let node = new Node(data) // new mode
    // stack is empty 
    if(!this.first){
      this.first = node
      this.last = node
    }else{
      node.next = this.first
      this.first = node
    }

    this.size++
  }

  pop(){

    if(!this.first) return null;

    let node = this.first

    if(node.next){
      node = node.next
      this.first = node
    }else{
      this.first = null
      this.last = null
    }
    this.size--
  }
}


