store.setState("workout",0);

function Experience(props) {
  const [experience, setExp] = useGlobalState("exp");
  let incrementExp = (e) => {
    setExp(experience+1)
  }
  let level = Math.floor(experience/10)
  return(
    <div>
      <h2>Level:{level}</h2>
    </div>
  )
}


// Each successful monster gives 5 EXP. 
