import { useState, useEffect, useRef } from 'react';
import React from 'react';
import './App.css';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';
import ReactDOM from 'react-dom'
import {useTrail, animated, useTransition, useSpring, useChain, config, useSpringRef} from 'react-spring'

function SomeOtherFunction() {
  const [name, setName] = useState("world");
  useEffect(()=>{
    setName(prompt("What is your name?"))
  }, [])
  return(<h1>Hello, {name}</h1>)
}

function Example() {
  const [count, setCount] = useState(0);
  return (
    <div> 
      <h1>You clicked {count} times</h1>

      <button onClick={() => setCount(count + 1 )}>
        Click Me
      </button>
    </div>
  );
}

function NewItemForm({onSubmit}) {
  const [subject, setSubject] = useState("");
  const [description, setDescription] = useState("");
  return(
      <div>
          <fieldset class="NewItem">
            <legend>Create a new item</legend>

            {/* This is the subject textbox */}
            <label htmlFor="subj">Subject</label>
            <input id="subj" value={subject} onChange={e=>{
              setSubject(e.target.value);
            }}/>

            {/* This is the subject textbox */}
            <label htmlFor="desc">description</label>
            <textarea id="desc" type="text" value={description} rows={5} onChange={e=>{
              setDescription(e.target.value);
            }}/>

            {/* This is the action button */}
            <label htmlFor="create"/>
            <button id="create" onClick={e=>{
              onSubmit( {subject:subject, description:description} );
              setSubject("");
              setDescription("");
            }}>Create item</button>
          </fieldset>
      </div>
  )
}

function ShowItem({item}) {
  return(
    <div className="ShowItem">
      <div className="left">Subject</div>
      <div className="right">{item.subject}</div>
      <div className="left">Description</div>
      <div className="right">{item.description}</div>
    </div>
  )
}

function ItemsList() {
  const [items, setItems] = useState([]);
  const addItem = (item) => {
    console.log("addItem received:", item);
    setItems([...items, item]);
  }

  console.log(items);
  return(
    <div className="ItemsList">
      {
        items.map((val, i) =>{
          return(<ShowItem key={i} item={val} /> )
        })
      }
      <NewItemForm onSubmit={addItem}/>
    </div>
  )
}

function Footer() {
  return(
    <div className="Footer">
        <li>Home</li>
        <li>Products</li>
        <li>Checkout</li>
        <li>About</li>
    </div>
  )
}

function Banner() {
  return(
    <div className="Banner">
      Hi
    </div>
  )
}

{/* linked list? */}
let Characteristics = [
  ['Health', 'Interaction', 'Concentration', 'Motivation', 'Efficiency'],
  [3, 5, 2, 0, 5],
  [3, 5, 2, 0, 5]
];

// Create class to contain stats
class Result {
  constructor(name, score) {
    this.name = name;
    this.score = score;
  }
}
// Define class to manage stats, a collection of result
class Scorebook {
  constructor(className) {
    this.className = className;
    this.results = []; //Initialise an empty array
  }
  // Add new result to collection
  addResult(name, score) {
    const r = new Result(name, score);
    this.results.push(r);
  }
  //Calculate the best stat from the results in our collection  ( latest stats)
  getTopScore() {
    let top_i = 0;
    this.results.forEach((result, i)=> {
      if (result.score > this.results[top_i].score) {
        top_i = i;
      }
    })
    return this.results[top_i].name;
  }
}

// Create instance of scorebook
let scorebook = new Scorebook("Identities");

scorebook.addResult("Health", 30);
scorebook.addResult("Concentration", 95);
scorebook.addResult("Efficiency", 90);
scorebook.addResult("Concentration", 38);
scorebook.addResult("Awesome", 74);

console.log(scorebook.getTopScore() );

function App() {

  return (

    <div> 
    <h1> System</h1>
    <p>{ Characteristics[1][1] }</p>

    <Banner />
          <a href="https://google.com" target="_blank" rel="noopener">Open Google</a> {/* This part of the code allows for a button that links to a separate website */}
          <audio src="minecraft.mp3" controls autoplay loop></audio>

    <ItemsList />
    <Footer />



    <particle/>

    </div>



  );
}

export default App;
