// Create class to contain stats
class Result {
  constructor(name, score) {
    this.name = name;
    this.score = score;
  }
}
// Define class to manage stats, a collection of result
class Scorebook {
  constructor(className) {
    this.className = className;
    this.results = []; //Initialise an empty array
  }
  // Add new result to collection
  addResult(name, score) {
    const r = new Result(name, score);
    this.results.push(r);
  }
  //Calculate the best stat from the results in our collection  ( latest stats)
  getTopScore() {
    let top_i = 0;
    this.results.forEach((result, i)=> {
      if (result.score > this.results[top_i].score) {
        top_i = i;
      }
    })
    return this.results[top_i].name;
  }
}

